mapper.select:
SELECT id, name, reserved1, reserved2
FROM st_course
WHERE id=#{id}

mapper.insert:
	INSERT INTO st_course(
	name, reserved1, reserved2
	) VALUES (
    #{name},
	#{reserved1},
	#{reserved2}
	)

mapper.update:
UPDATE st_course SET
name = #{name},
reserved1 = #{reserved1},
reserved2 = #{reserved2}
WHERE id = #{id}
