mapper.insert:
insert into sms_book (id, name, author,
create_by, create_date, update_by,
update_date, remarks, del_flag
)
values (#{id}, #{name}, #{author},
#{createBy}, #{createDate}, #{updateBy},
#{updateDate}, #{remarks}, #{delFlag}
)

mapper.update:
UPDATE sms_book SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
